import React from 'react'
import Search from './components/Search/index'
import Results from './components/Results/index'
import './App.css'

class App extends React.Component{

    constructor(props){
        super(props);
            this.state = {
                query: "",
                is_query_movie: true,
                display: false
            }
    }
    handle = (e) => {
        this.setState({query:e});
    }

    movies_or_shows = (e) => {
        this.setState({is_query_movie:e});
    }


    render(){

        return(
            <div className = 'App'>
                <h1 className='header_title'> Search Movies/TV-Shows</h1>
                <Search search_key={this.handle} is_query_movie={this.movies_or_shows}/>
                {this.state.display ? <Results query={this.state.query} is_query_movie={this.state.is_query_movie} display={this.state.display}/> : null}
            </div>
        )
    }
};

export default App;
