import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'



class Details extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            id: this.props.id,
            results: []
        }
    }

    async componentDidMount(){
        const url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US&append_to_response=videos');
        const response = await fetch(url);
        const data = await response.json();
        this.setState({results: data});
    }

    render(){
        const trailer_link = 'https://www.youtube.com/watch?='
        console.log(this.state.videos);
        return(
            <div>
                <Row className='movie_title'> 
                    <Col>
                        <h1>{this.state.results.original_title}</h1> 
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <p1>{this.state.results.overview}</p1>
                    </Col>
                </Row>
                <Row>
                    <Col>
                    {this.state.results.videos.map(video => (
                        <Col key = {video.id} className = 'Trailers'>
                            <video id='trailer'> 
                                <source src={trailer_link.concat(video.key)} type="video/mp4" />
                            </video>
                        </Col>
                    ))}
                    </Col>
                </Row>
            </div>
        )

    }
};


export default Details;
