import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './HomePage.css'

class Home extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            movies:[],
            loading:false,
            search:""
        }
    }
    async componentDidMount(){
        const url = "https://api.themoviedb.org/3/trending/all/day?api_key=4f02959b89ceb660d5b1018f517488de";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data.results);
        this.setState({movies: data.results, loading:false});
    }

    render(){
        var img_url = 'https://image.tmdb.org/t/p/w300';
        if(this.state.loading || !this.state.movies){
            console.log('Testing')
        }
        return( 
            <div>
                <Container className = "homepage">
                    <Row className = "headers">
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        <Col>
                            <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." /> </Col>
                    </Row>
                    <Row>
                    {this.state.movies.map(movie => (
                        <Col key = {movie.id} className = 'Posters'> 
                            <img src = {img_url.concat(movie.poster_path)} alt = 'Posters' />
                            <p className = 'MovieName'> {movie.name}</p>
                            <p className = 'MovieTitle'> {movie.title}</p>
                            <p>{movie.id} </p> 
                        </Col>
                    ))}
                    </Row>
                </Container>
            </div>    
        )
    }
};
export default Home;
