import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './styles.css'
import ReactPlayer from "react-player"
import { Link } from 'react-router-dom'


class Details extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            id: this.props.id,
            details: [],
            video_results: {}
        }
    }

    async componentDidMount(){
        const url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US&append_to_response=videos');
        const response = await fetch(url);
        const data = await response.json();
        this.setState({details: data});
        this.setState({video_results: data.videos.results})
    }

    render(){
        const trailer_link = 'https://www.youtube.com/watch?v='
        return(
            <div>
                <Row className='movie_title'> 
                    <Col>
                        <h1>{this.state.details.original_title}</h1> 
                    </Col>
                </Row>
                <Row className='overview'>
                    <Col>
                        <p1>{this.state.details.overview}</p1>
                    </Col>
                </Row>
                <Row>
                    <Col>
                    <h1>Trailer</h1> 
                    {this.state.video_results.length > 0 ? this.state.video_results.map(vids =>
                        vids.type == "Trailer" ? <ReactPlayer url={trailer_link.concat(vids.key)} controls={true}/> : null
                    ) : null}
                    </Col>
                </Row>
            </div>
        )

    }
};


export default Details;
