import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './styles.css'
import ReactPlayer from "react-player"
import { Link } from 'react-router-dom'


class Details extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            id: this.props.id,
            details: [],
            video_results: {}
        }
    }

    async componentDidMount(){
        const url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US&append_to_response=videos');
        const response = await fetch(url);
        const data = await response.json();
        this.setState({details: data});
        this.setState({video_results: data.videos})
    }

    render(){
        const trailers = {
            "results": [
                {
                    "id": "5f0f0e369672ed00364c4619",
                    "iso_639_1": "en",
                    "iso_3166_1": "US",
                    "key": "xw1vQgVaYNQ",
                    "name": "Project Power starring Jamie Foxx | Official Trailer | Netflix",
                    "site": "YouTube",
                    "size": 1080,
                    "type": "Trailer"
                },
                {
                    "id": "5f1b6d501b1f3c00386e867d",
                    "iso_639_1": "en",
                    "iso_3166_1": "US",
                    "key": "KZOdwr0zp1M",
                    "name": "Comic-Con Project Power Clip: Joseph Gordon Levitt is Bulletproof | Netflix",
                    "site": "YouTube",
                    "size": 1080,
                    "type": "Clip"
                }
            ]
        }
        const trailer_link = 'https://www.youtube.com/watch?='
        return(
            <div>
                <Row className='movie_title'> 
                    <Col>
                        <h1>{this.state.details.original_title}</h1> 
                    </Col>
                </Row>
                <Row className='overview'>
                    <Col>
                        <p1>{this.state.details.overview}</p1>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        {console.log(trailers.results)}
                    </Col>
                </Row>
            </div>
        )

    }
};


export default Details;
