import React from 'react'
import Home from './components/Homepage/index'
import Search from './components/Search/index'
import Results from './components/Results/index'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './App.css'
import {Route, Link, Switch, BrowserRouter as Router, withRouter} from 'react-router-dom'

class App extends React.Component{

    constructor(props){
        super(props);
            this.state = {
                query: "",
                is_query_movie: true,
                hide_parts: true
            }
    }
    handle = (e) => {
        this.setState({query:e});
    }

    movies_or_shows = (e) => {
        this.setState({is_query_movie:e});
    }


    render(){
        return(
            <div className = 'App'>
                <h1 className='header_title'> Search Movies/TV-Shows</h1>
                <Search search_key={this.handle} is_query_movie={this.movies_or_shows}/>
                <Results query={this.state.query} is_query_movie={this.state.is_query_movie}/>
            </div>
        )
    }
};

export default App;
