import React from 'react'
import Home from './components/HomePage'
import Search from './components/Search'
import Results from './components/Results'
import Details from './components/Details'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './App.css'
import {Route, Link, withRouter, Switch} from 'react-router-dom'

class App extends React.Component{

    constructor(){
        super();
            this.state = {
                query: "",
                hide: false
            }
    }
    handle = (e) => {
        this.setState({query:e});
    }
    render(){
        return(
            <div className = 'App'>
                <Container className = 'headers'>
                 <Row className = "headers">
                        <Col>
                            <Link to='/'>Showtime</Link>
                        </Col>
                        <Col>
                            <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row> 
                </Container>
                <Search search_key={this.handle}/>
                <Results query={this.state.query}/>
                <Switch>
                <Route path='/results' component={withRouter(Results)}/>
                <Route path='/query' component={withRouter(Details)}/>
                <Route exact path='/' component={withRouter(Home)} />
                </Switch>
            </div>
        )
    }
};

export default App;
