import React from 'react'
import Home from './components/Homepage/index'
import Search from './components/Search/index'
import Results from './components/Results/index'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './App.css'
import {Route, Link, Switch, BrowserRouter as Router, withRouter} from 'react-router-dom'

class App extends React.Component{

    constructor(){
        super();
            this.state = {
                query: "",
                menu_clicked: false
            }
    }
    handle = (e) => {
        this.setState({query:e});
    }

    render(){
        return(
            <div className = 'App'>
                <h1 className='header_title'> Search Movies/TV-Shows</h1>
                <Search search_key={this.handle}/>
                <Results query={this.state.query}/>
                {this.state.menu_clicked ? (<Home/>): (null)}

            </div>
        )
    }
};

export default App;
