import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './styles.css'
import ReactPlayer from "react-player"
import { Link } from 'react-router-dom'


class Details extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            id: this.props.id,
            details: [],
            video_results: {},
            cast: [],
            crew_memebers: [],
            genres: {}
        }
    }

    async componentDidMount(){
        const url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US&append_to_response=videos');
        const response = await fetch(url);
        const data = await response.json();
        this.setState({details: data});
        this.setState({video_results: data.videos.results})
        const cast_url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('/credits?api_key=4f02959b89ceb660d5b1018f517488de');
        const response_cast = await fetch(cast_url);
        const data_cast = await response_cast.json();
        this.setState({cast: data_cast.cast})
        this.setState({crew_memebers: data_cast.crew})
        this.setState({genres: data.genres});
    }

    render(){
        const trailer_link = 'https://www.youtube.com/watch?v='
        return(
            <div className = 'detail_background'>
                <Row className='movie_title'> 
                    <Col>
                        <h1>{this.state.details.original_title}</h1> 
                    </Col>
                </Row>

                <Row className= 'movie_details'>
                    <Col>
                        Directed By: 
                        {this.state.crew_memebers.map(crew => 
                            crew.job === 'Director'? <p1> {crew.name} </p1>: null
                        )}
                    </Col>
                    <Col>
                            <p1> Release Date: {this.state.details.release_date}</p1>
                    </Col> 
                    <Col>
                            <p1> Runtime: {this.state.details.runtime} min</p1>
                    </Col>   

                </Row>

                <Row className='genres'> 
                    <Col>
                        Genre: 
                        {this.state.genres.length > 0 ? this.state.genres.map(genre =>
                            <li> {genre.name} </li> 
                        ) : null}
                    </Col>

                </Row> 

                <Row  className='overview'>
                    <Col>
                        <p1 className='overview'> Overview: {this.state.details.overview}</p1>
                    </Col>

                    <Col className='poster'> 
                            <img src={'https://image.tmdb.org/t/p/w200'.concat(this.state.details.poster_path)} alt='Poster' />
                    </Col>
                </Row> 

                <Row> 
                    <Col> 
                        {this.state.cast.slice(0,9).map(actor => (
                            <li>{actor.name}</li> 
                        ))}
                    </Col>
                </Row>

                <Row className='detail_page'>
                        <div class="col-md-12">
                        {this.state.video_results.length > 0 ? this.state.video_results.map(vids =>
                           <ReactPlayer className = 'trailer' url={trailer_link.concat(vids.key)} controls={true}/>
                        ) : null}
                        </div>
                </Row>
            </div>
        )

    }
};


export default Details;
