import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import {Link} from 'react-router-dom'
import './Search.css'

class Search extends React.Component{

constructor(props){
    super(props);
    this.state = {
        query: "",
        results: [],
        loading: false,
    }
}

    async componentDidMount(){
        const url = "https://api.themoviedb.org/3/trending/all/day?api_key=4f02959b89ceb660d5b1018f517488de";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({movies: data.results, loading:false});
    }

render(){

    return(
        <div> 
        <Container>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." onChange={e => this.setState({ query: e.target.value })} />
                        </Col>
                    </Row>
                    <Row className = 'search_buttons'> 
                        <Col> 
                            <Link to = '/search'> 
                                <button OnClick={<Search keysearch={this.clicked}/>}> Search Movie </button>
                            </Link>
                        </Col>

                        <Col>
                            <Link to = '/search'>  
                                <button OnClick={this.clicked}> Search TV-Series</button>
                            </Link>
                        </Col>

                    </Row>
            </Container>
        </div>
        )
    }
};

export default Search;
