import React from 'react'
import Home from './components/Homepage/index'
import Search from './components/Search/index'
import Results from './components/Results/index'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './App.css'
import {Route, Link, Switch, BrowserRouter as Router, withRouter} from 'react-router-dom'

class App extends React.Component{

    constructor(){
        super();
            this.state = {
                query: ""
            }
    }
    handle = (e) => {
        this.setState({query:e});
    }

    render(){
        return(
            <div className = 'App'>
                <Container className = 'headers'>
                 <Row className = "headers">
                        <Link to ="/">
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        </Link>
                        <Col>
                            <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row> 
                </Container>
                    <Search search_key={this.handle}/>
                    <Results query={this.state.query}/>
                <Router> 
                    <Switch>
                        <Route exact path='/' component={Home} />
                        <Route exact path='/results' component={Results}/>
                    </Switch>
                </Router>
            </div>
        )
    }
};

export default App;
