import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './HomePage.css'

class Home extends React.Component{


    APIRequest(){
        let request  = newXMLHttpRequest();
        request.open("GET", "https://api.themoviedb.org/3/search/company?api_key=4f02959b89ceb660d5b1018f517488de&query=Home&page=1")
        request.send();
        request.onload = () => {
            if(request.status == 200){
                return console.log("It Worked")
            }
            else{
                return console.log("It did not work")
            }
        }
    }




    render(){
        return( 
            <div>
                <Container className = "homepage">
                    <Row className = "headers">
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        <Col>
                        <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." /> </Col>
                    </Row>
                </Container>
            </div>    
        )
    }
};

export default Home;
