    constructor(props){
        super(props);
        this.state = {
            result: [],
            loading: false,
            val:""
        }
}



    render(){

        importData()
            fetch('https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query='.concat(this.props.query))
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({results:res.items, loading:false});
                    }
                )





        return(

            <div>
                <h1> {this.props.query} </h1>


            </div>

        )
    }

};

