import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './HomePage.css'

class Home extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            movies:[],
            loading:false,
            query: "",
            clicked:false
        }
    }

    OnChangeHandler = (event) => {
        const query = event.target.value;
        this.setState({query:query});
    }

    async componentDidMount(){
        var url = "";
        if(this.state.query === ""){
            url = "https://api.themoviedb.org/3/trending/all/day?api_key=4f02959b89ceb660d5b1018f517488de";
        }
        else{
            url = "https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query=".concat(this.state.query);
        }
        console.log(This.state.query)
        const response = await fetch(url);
        const data = await response.json();
        this.setState({movies: data.results, loading:false});
    }

    render(){
        var img_url = 'https://image.tmdb.org/t/p/w300';
        if(this.state.loading || !this.state.movies){
            console.log('Loading');
        }
        return( 
            <div>
                <Container className = 'background'>
                    <Row className = "searchbar">
                        <Col> 
                            <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." onChange={this.OnChangeHandler} /> 
                        </Col>
                    </Row>
                    <Row className = 'search_buttons'> 
                        <Col> 
                            <button onClick={this.clicked}> Search Movie </button>
                        </Col>

                        <Col>
                            <button onClick={this.clicked}> Search TV-Series</button>
                        </Col>
                    </Row>
                    <Row>
                    {this.state.movies.map(movie => (
                        <Col key = {movie.id} className = 'Posters'> 
                            <button className = 'trending'>
                                <img  src = {img_url.concat(movie.poster_path)} alt = 'Posters' /> 
                            </button>
                            <p className = 'MovieName'> {movie.name}</p>
                            <p className = 'MovieTitle'> {movie.title}</p>
                        </Col>
                    ))}
                    </Row>
                </Container>
            </div>    
        )
    }
};
export default Home;
