import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

class Search extends React.Component{

constructor(props){

    super(props);
    this.state = {
        search_results: [],
        loading: false,
    }
}



async componentDidMount(){

    const search_url = "https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query=knight";
    const response = await fetch(search_url);
    const search_data = await response.json();
    console.log(search_data.results)
    this.setState({search_results: search_data.results, loading:false})

}



render(){

    var img_url = 'https://image.tmdb.org/t/p/w300';
    if(this.state.loading || !this.state.search_results){
        console.log('Loading');
    }

    return(
        <div> 
            <Container className = 'background'>
                <Row>
                    <Col> 
                        Search Results:
                    </Col>
                </Row>  
                <Row>
                    {this.state.search_results.map(search_result => (
                        <Col key = {search_result.id} className = 'Posters'> 
                            <img src = {img_url.concat(search_result.poster_path)} alt = 'Posters' />
                            <p className = 'MovieName'> {search_result.name}</p>
                            <p className = 'MovieTitle'> {search_result.title}</p>
                        </Col>
                    ))}
                </Row> 
            </Container> 
        </div>
        )
    }
};


export default Search
