import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import {Link} from 'react-router-dom'
import './styles.css'


class Results extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            result: [],
            loading: false,
            is_query_movie: this.props.is_query_movie,
            offset: 0,
            per_page: 0,
            current_page: 1,
            page_count: 0
        }
}
    async componentDidUpdate(prevProps){
        this.fetchingData( )
    }


    fetchingData = async (prevProps) => {
        if(prevProps.query !== this.props.query || prevProps.is_query_movie !== this.props.is_query_movie){
            let url = ''
            if(this.props.is_query_movie){
                url = 'https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query='.concat(this.props.query).concat('&page=1');

            }
            else{
                url = 'https://api.themoviedb.org/3/search/tv?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US&page=1&query='.concat(this.props.query).concat('&include_adult=false')
            }
            const response = await fetch (url);
            const data = await response.json();
            this.setState({result: data.results, loading:false});
            this.setState({page_count: Math.ceil(data.total_results/data.total_pages)})

        }
    }



    render(){
            if(this.state.loading || !this.state.movies){
                    console.log('')
            }
            console.log(this.state)
            var img_url = 'https://image.tmdb.org/t/p/w300';
            return(
            <div>
                <Container className = 'background'>
                <Row>
                {this.state.result.map(movie => (
                    <Col key = {movie.id} className = 'Posters'> 
                        <Link to='/query'>
                        <button className = 'trending'>
                            <img  src = {img_url.concat(movie.poster_path)} alt = 'Posters' /> 
                        </button>
                        </Link>
                        <p className = 'MovieName'> {movie.name}</p>
                        <p className = 'MovieTitle'> {movie.title}</p>
                    </Col>
                ))}
                </Row>


            </Container>
            </div>
        )
    }
};

export default Results;
