        super(props);
        this.state = {
            id: this.props.id
        }
    }

    async componentDidMount(){
        const url = 'https://api.themoviedb.org/3/movie/'.concat(this.state.id).concat('?api_key=4f02959b89ceb660d5b1018f517488de&language=en-US')
        console.log(url)
    }

    render(){
        return(
            <div>
                    <h1> Movie Details</h1>
            </div>
        )

    }
};


