import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './HomePage.css'

class Home extends React.Component{

    state = {
        movie:null,
        loading:false
    }

    async componentDidMount(){
        const url = "https://api.themoviedb.org/3/trending/all/day?api_key=4f02959b89ceb660d5b1018f517488de";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data.results);
        this.setState({movie: data.results, loading:false});
    }

    render(){

        var msg;
        var posters = [];
        var base_url = 'https://image.tmdb.org/t/p/w200';
        if(this.state.loading || !this.state.movie){
            msg = "Loading";
        }
        else{
            var i = 0;
            while(i<20){
                posters[i] = base_url.concat(this.state.movie[i].poster_path);
                ++i;
            }
        }
        return( 
            <div>
                <Container className = "homepage">
                    <Row className = "headers">
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        <Col>
                        <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." /> </Col>
                    </Row>
                    <Row className = 'Row1'>
                        <Col> <img src = {posters[0]} /> </Col>
                        <Col> <img src = {posters[1]} /> </Col>
                        <Col> <img src = {posters[2]} /> </Col>
                        <Col> <img src = {posters[3]} /> </Col>
                    </Row> 
                    <Row clasName = 'Row2'> 
                        <Col> <img src = {posters[4]} /> </Col>
                        <Col> <img src = {posters[5]} /> </Col>
                        <Col> <img src = {posters[6]} /> </Col>
                        <Col> <img src = {posters[7]} /> </Col>
                    </Row>
                    <Row className = 'Row3'> 
                        <Col> <img src = {posters[8]} /> </Col>
                        <Col> <img src = {posters[9]} /> </Col>
                        <Col> <img src = {posters[10]} /> </Col>
                        <Col> <img src = {posters[11]} /> </Col>
                    </Row>
                    <Row className = 'Row4'> 
                        <Col> <img src = {posters[12]} /> </Col>
                        <Col> <img src = {posters[13]} /> </Col>
                        <Col> <img src = {posters[14]} /> </Col>
                        <Col> <img src = {posters[15]} /> </Col>
                    </Row>
                    <Row> 
                        <Col> <img src = {posters[16]} /> </Col>
                        <Col> <img src = {posters[17]} /> </Col>
                        <Col> <img src = {posters[18]} /> </Col>
                        <Col> <img src = {posters[19]} /> </Col>
                    </Row>
                </Container>
            </div>    
        )
    }
};

export default Home;
