import React from 'react'
import Home from './components/HomePage'
import Results from './components/Results'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './App.css'
import {Route, Link} from 'react-router-dom'

class App extends React.Component{

    constructor(props){
        super(props);
            this.state = {
                query: ""
            }
    }
    handle(event){
        this.setState({query: event.target.value});
    }
    mutateQuery(search_query){
        this.setState({query: search_query});
    }
    render(){
        console.log(this.state.query);
        return(
            <div className = 'App'>
                <Container className = 'headers'>
                 <Row className = "headers">
                        <Link to = '/'>
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        </Link>
                        <Col>
                            <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                            <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row> 
                </Container> 

                <Row className = "searchbar">
                    <Col> 
                        <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." onChange={this.handle.bind(this)} /> 
                    </Col>
                </Row>
                <Results search_query={this.state.query} mutateState={this.mutateQuery} />
                <Link to='/results'> 
                    <Row className = 'search_buttons'> 
                        <Col> 
                            <button onClick={this.clicked}> Search Movie </button>
                        </Col>
                        <Col>
                            <button onClick={this.clicked}> Search TV-Series</button>
                        </Col>
                    </Row> 
                </Link> 
                <Route exact path="/" component={Home} />
                <Route exact path="/results" component={Results}/>
            </div>
        )
    }
};

export default App;
