import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import {Link} from 'react-router-dom'
import './styles.css'


class Results extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            result: [],
            loading: false,
            appear: true,
            show_results: true
        }
}
    async componentDidUpdate(prevProps){
        if(prevProps.query !== this.props.query){
            const url = 'https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query='.concat(this.props.query);
            const response = await fetch (url);
            const data = await response.json();
            this.setState({result: data.results, loading:false, appear:this.props.hide, show_results: this.props.main_menu});
        }
    }
    render(){
        {console.log(this.state.show_results)}
        if(this.state.appear){
            return null;
        }
        else if(!this.props.main_menu){
            return null
        }
        else{
            if(this.state.loading || !this.state.movies){
                console.log('Loading');
            }
            var img_url = 'https://image.tmdb.org/t/p/w300';
            return(

                <div>
                    <Container className = 'background'>
                    <Row>
                    {this.state.result.map(movie => (
                        <Col key = {movie.id} className = 'Posters'> 
                            <Link to='/query'>
                            <button className = 'trending'>
                                <img  src = {img_url.concat(movie.poster_path)} alt = 'Posters' /> 
                            </button>
                            </Link>
                            <p className = 'MovieName'> {movie.name}</p>
                            <p className = 'MovieTitle'> {movie.title}</p>
                        </Col>
                    ))}
                    </Row>
                </Container>
                </div>
            )
        }
    }
};

export default Results;
