import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import {Link} from 'react-router-dom'
import Results from './Results'
import Home from './HomePage'

class Search extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            query_val:"",
            hidden:true
        }
    }


    SubmitMovieQuery = (e) => {
        this.props.search_key(this.state.query_val);
        this.setState({movies_search:true});
        this.props.movies_search(true);
    }

    SubmitShowsQuery = (e) => {
        this.props.search_key(this.state.query_val);
        this.props.movies_search = false;
    }

    OnQueryChange = (e) => {
        this.setState({query_val: e.target.value});
    }

    render(){       
        return(
            <div>
            <Container> 
            <Row className = "searchbar">
                <Col> 
                    <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." onChange={this.OnQueryChange} /> 
                </Col>
            </Row>
            <Link to='/results'> 
                    <Row className = 'search_buttons'> 
                        <Col> 
                            <button onClick={this.SubmitMovieQuery}> Search Movie </button>
                        </Col>
                        <Col>
                            <button onClick={this.SubmitShowsQuery}> Search TV-Series</button>
                        </Col>
                </Row> 
            </Link>
            <Results/>
            </Container>
        </div>
        )
    }
};

export default Search;
