import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import {Link} from 'react-router-dom'
import './Search.css'

class Search extends React.Component{

constructor(props){
    super(props);
    this.state = {
        query: "",
        results: [],
        loading: false,
    }
}


    OnChangeHandler = (event) => {
        const query = event.target.value;
        this.setState({query:query});
    }


    componentDidMount(){
        let url = 'https://api.themoviedb.org/3/search/movie?api_key=4f02959b89ceb660d5b1018f517488de&query='.concat(this.state.query);
        fetch(url, {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            }
        }).then((result) =>{
            result.json().then((resp)=>{
                this.setState({results:resp})
            })
        })
    }

render(){
    const data = this.state.results;
    console.log(data);
    return(
        <div>
        <Container>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." onChange={this.OnChangeHandler} /> 
                        </Col>
                    </Row>
                    <Row className = 'search_buttons'> 
                        <Col> 
                            <Link to = '/search'> 
                                <button onClick={this.OnChangeHandler}> Search Movie </button>
                            </Link>
                        </Col>

                        <Col>
                            <Link to = '/search'>  
                                <button onClick={this.clicked}> Search TV-Series</button>
                            </Link>
                        </Col>

                    </Row>
            </Container>
        </div>
        )
    }
};

export default Search;
