import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './HomePage.css'

class Home extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            movie:null,
            loading:false,
            rows: 5
        }
    }
    async componentDidMount(){
        const url = "https://api.themoviedb.org/3/trending/all/day?api_key=4f02959b89ceb660d5b1018f517488de";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data.results);
        this.setState({movie: data.results, loading:false});
    }

    render(){

        function InsertRows(){
            var movieRows = [];
            if(this.state.loading || !this.state.movie){
                console.log('Loading');
            }
            else{
                this.state.movie.foreach((movie) => {
                    console.log('It Worked');
                })
            }
        }

        return( 
            <div>
                <Container className = "homepage">
                    <Row className = "headers">
                        <Col>
                            <button onClick ={this.clicked}> Showtime </button>
                        </Col>
                        <Col>
                        <button onClick ={this.clicked}> Movies </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> TV-Shows </button>
                        </Col>
                        <Col> 
                        <button onClick ={this.clicked}> Latest Releases </button>
                        </Col>
                    </Row>
                    <Row className = "searchbar">
                        <Col> <input type="text" className="searchmovies" placeholder="Find Your Movie/Show..." /> </Col>
                    </Row>
                    {InsertRows()}
                </Container>
            </div>    
        )
    }
};

export default Home;
